mapper.update:
UPDATE t_book
   SET b_name = #{b_name}, b_author = #{b_author}, b_publish = #{b_publish}, b_pdate = #{b_pdate}
 WHERE b_id = #{b_id}
   AND u_id = #{u_id}

UPDATE t_book
   SET b_check = 1,b_checkdate = CURDATE()
 WHERE b_id = #{b_id}

UPDATE t_book
   SET b_check = 3,b_date = CURDATE()
 WHERE b_id = #{b_id}

UPDATE t_book
   SET b_check = 2,b_checkdate = CURDATE()
 WHERE b_id = #{b_id}
