mapper.select:
SELECT u_id,u_account,u_name,u_pwd,u_class,u_sex,u_phone,u_email,u_role
  FROM t_user
 WHERE u_account=#{u_account}
   AND u_pwd=#{u_pwd}
   AND u_role=#{u_role}

mapper.update:
UPDATE t_user 
   SET u_pwd = #{u_pwd}
 WHERE u_id = #{u_id}

UPDATE t_user

  SET u_pwd = #{u_pwd}


   SET u_name = #{u_name},u_sex = #{u_sex},u_phone = #{u_phone},u_email = #{u_email}

 WHERE u_id = #{u_id}
